userDefinedLinkHintCss:
div > .vimiumHintMarker {
  /* Smaller, thinner, dark mode hint box with orange accent */
  background: #0f1419;
  border: 1px solid #ff8f40;
  border-radius: 2px;
  box-shadow: 0 1px 4px rgba(0,0,0,0.4);
  z-index: 999999;
  padding: 0px 3px;
  min-width: 12px;
  min-height: 14px;
  display: inline-block;
}

div > .vimiumHintMarker span {
  /* Smaller, bold, orange text */
  color: #ff8f40;
  font-weight: bold;
  font-size: 10px;
  letter-spacing: 0.2px;
  text-shadow: 0 1px 1px #0f1419;
  line-height: 1.1;
}

div > .vimiumHintMarker > .matchingCharacter {
  /* Inverted highlight for typed character, still small */
  color: #0f1419;
  background: #ff8f40;
  border-radius: 1px;
  padding: 0 1px;
  font-size: 10px;
}
