userDefinedLinkHintCss:
/* Helix Ayu Dark */
:root {
  --vimium-background: #0f1419;
  --vimium-foreground: #bfbdb6;
  --vimium-black: #131721;
  --vimium-dark-gray: #2d3640;
  --vimium-gray: #5c6773;
  --vimium-orange: #ff8f40;
  --vimium-yellow: #e6b450;
  --vimium-white: #ffffff;
}

/* Hint Markers */

#vimiumHintMarkerContainer div.internalVimiumHintMarker,
#vimiumHintMarkerContainer div.vimiumHintMarker {
  background: var(--vimium-background);
  border: 1px solid var(--vimium-orange);
  border-radius: 2px;
  box-shadow: 0 1px 4px rgba(0,0,0,0.4);
  z-index: 999999;
  padding: 0px 3px;
  min-width: 12px;
  min-height: 14px;
  display: inline-block;
}

#vimiumHintMarkerContainer div span {
  color: var(--vimium-orange);
  font-weight: bold;
  font-size: 10px;
  letter-spacing: 0.2px;
  text-shadow: 0 1px 1px var(--vimium-background);
  line-height: 1.1;
}

#vimiumHintMarkerContainer div > .matchingCharacter {
  color: var(--vimium-background);
  background: var(--vimium-orange);
  border-radius: 1px;
  padding: 0 1px;
  font-size: 10px;
}

#vimiumHintMarkerContainer div > .matchingCharacter ~ span {
  color: #bfbdb6;
}

/* Vomnibar Popup */
#vomnibar {
  background: var(--vimium-background);
  border: 1px solid var(--vimium-orange);
  animation: show 200ms cubic-bezier(0, 0, 0.2, 1) forwards;
  max-height: calc(100vh - 70px);
  overflow: hidden;
}

@keyframes show {
  0% {
    transform: translateY(50px);
    opacity: 0;
  }
  100% {
    transform: translateY(0);
    opacity: 1;
  }
}

#vomnibar input {
  color: var(--vimium-white);
  background: var(--vimium-black);
  border: none;
  height: unset;
  padding: 16px 30px;
}

#vomnibar .vomnibarSearchArea {
  border: none;
  padding: unset;
  background: var(--vimium-background);
}

#vomnibar ul {
  padding: 0;
  margin: 0;
  background: var(--vimium-background);
  border-top: 1px solid var(--vimium-dark-gray);
}

#vomnibar li {
  padding: 10px;
  border-bottom: 1px solid var(--vimium-dark-gray);
  color: var(--vimium-white);
  background: transparent;
}

#vomnibar li .vomnibarTopHalf,
#vomnibar li .vomnibarBottomHalf {
  padding: 3px 0;
}

/* Source label */
#vomnibar li .vomnibarSource {
  color: var(--vimium-orange);
}

/* Title and emphasis: use white */
#vomnibar li em,
#vomnibar li .vomnibarTitle {
  color: var(--vimium-white);
  font-weight: bold;
}

/* URL: use gray unless match, which is orange */
#vomnibar li .vomnibarUrl {
  color: var(--vimium-gray);
}
#vomnibar li .vomnibarUrl .vomnibarMatch {
  color: var(--vimium-orange);
  font-weight: bold;
}

/* Match: use yellow for high emphasis, orange for title match */
#vomnibar li .vomnibarMatch {
  color: var(--vimium-yellow);
  font-weight: bold;
}
#vomnibar li .vomnibarTitle .vomnibarMatch {
  color: var(--vimium-orange);
}

/* Selected entry: strong dark gray highlight */
#vomnibar li.vomnibarSelected {
  background-color: var(--vimium-dark-gray);
  color: var(--vimium-white);
}

/* HUD and inputs */
div.vimiumHUD {
  background: var(--vimium-background);
  border: none;
  box-shadow: 0 1px 3px rgba(19, 23, 33, 0.12), 0 1px 2px rgba(19, 23, 33, 0.24);
}

div.vimiumHUD span#hud-find-input,
div.vimiumHUD .vimiumHUDSearchAreaInner {
  color: var(--vimium-white);
}

div.vimiumHUD .hud-find {
  background-color: var(--vimium-background);
  border: none;
}

div.vimiumHUD .vimiumHUDSearchArea {
  background-color: var(--vimium-background);
}

body.vimiumBody, .vimiumBody {
  background-color: var(--vimium-background) !important;
}

.vimiumBody #footerWrapper, #userDefinedLinkHintCss, #scrollStepSize, #linkHintNumbers, #newTabUrl, #nextPatterns, #previousPatterns, #searchUrl, #linkHintCharacters, #searchEngines, #keyMappings, #uploadBackup {
  color: var(--vimium-white) !important;
  background-color: var(--vimium-dark-gray);
}

.vimiumBody #footer, .vimiumBody .remove, input[name="pattern"], input[name="passKeys"], #uploadBackup {
  background-color: var(--vimium-dark-gray) !important;
  color: var(--vimium-white) !important;
}

body.vimiumBody input {
  border-color: var(--vimium-black);
}

body.vimiumBody a {
  color: var(--vimium-orange);
}

body.vimiumBody .exclusionHeaderText, body.vimiumBody .example, body.vimiumBody .caption, body.vimiumBody header,
body.vimiumBody textarea, body.vimiumBody input, body.vimiumBody label {
  color: var(--vimium-white) !important;
}
